import { useState } from "react";

export default function Footer ({}) {

  let tit = ['매칭','마음','약속','프로필']
  let [action,setAction] = useState([0,0,0,0]) 

  return (
    <footer className="footer">
      <div className="footer-wrap">
        <ul>
          {
            tit.map((x,i)=>{
              return (
                <li className={action[i]} key={i} onClick={()=>{
                  setAction([0,0,0,0])
                  let copy = [...action]
                  copy[i] = 1
                  setAction(copy)
                }}>
                  <a href="">{x}</a>
                </li>
              )
            })
          }
          <li>
            <a href="#">
              매칭
            </a>
          </li>
        </ul>
      </div>
    </footer>
  );
}
