import PropTypes from "prop-types"

function Button({label,backgroundColor,color,size = "md",fontWeight, onClick,border,borderRadius}) {
  let scale = 1
  if(size === "sm") scale = 0.75
  if(size === "lg") scale = 1.5
  let bd;
  border === "has" ? bd = "1px solid" : border === "none" ? bd = "none" : bd = null;
  
  const style = {
    backgroundColor,
    padding : `${scale * 0.5}rem ${scale * 1}rem`,
    border : bd,
    color,
    fontWeight,
    borderRadius
  }
  return (
    <button onClick={onClick} style={style} >
      {label}
    </button>
  )
}

Button.propTypes = {
  label : PropTypes.string,
  backgroundColor : PropTypes.string,
  size : PropTypes.oneOf(["sm", "md", "lg"]),
  color : PropTypes.string,
  fontWeight : PropTypes.string,
  onClick : PropTypes.func,
  border : PropTypes.oneOf(["has", "none"]),
  // border : PropTypes.bool, 불린으로 쓰는법
  borderRadius : PropTypes.string
}

export default Button
