import type { Meta, StoryObj } from '@storybook/react';
import Button from '../components/Button';

const meta = {
  title : 'components/stories/Button',
  component : Button,
  tags : ['autodocs'],
  argTypes : {
    color : {
      control : 'radio',
      options:['black','red','blue']
    },
    fontWeight : {
      control : 'radio',
      options:[400,500,700]
    }
  }
}

export default meta;
type Story = StoryObj<typeof meta>;

export const DefaultButton: Story = {
  args: {
  }
}
