import { useState } from "react";

export default function Footer ({}) {

  let tit = ['매칭','마음','약속','프로필']
  let [action,setAction] = useState([0,0,0,0]) 
  let initAction = () => {
    let init = [0,0,0,0]
    setAction(init)
  }
  return (
    <footer className="footer">
      <div className="footer-wrap">
        <ul>
          {
            tit.map((x,i)=>{
              return (
                <li className={action[i] == 1 ? 'action' : null} key={i} onClick={()=>{
                  initAction()
                  let copy = [...action]
                  copy[i] = 1
                  setAction(copy)
                }}>
                  <a href="" onClick={(e)=>{e.preventDefault()}}>{x}</a>
                </li>
              )
            })
          }
        </ul>
        {action}
        <button onClick={()=>{setAction([0,0,0,0])}}>aaa</button>
      </div>
    </footer>
  );
}
